import { Component } from '@angular/core';
import { MyserviceService } from './services/myservice.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'registrationproject';

  name:string="this is my data";
  obj={
    fname:"vishal",
    lname:"deore"
   }
  whatToShow:number=0;
  display(num:number){
    this.whatToShow=num;
  }
  getDataFromChild(eventVal:string){
    console.log("app com : "+eventVal);
    
  }
  constructor(public service:MyserviceService){

  }
  setData(valueFromHtml:string){
         this.service.setName(valueFromHtml);
  }
}
