import { Component } from '@angular/core';
import { User } from 'src/app/model/User';

@Component({
  selector: 'app-userregistraion',
  templateUrl: './userregistraion.component.html',
  styleUrls: ['./userregistraion.component.css']
})
export class UserregistraionComponent {

  reguserdata:User[]=[];
  name:string="";
    address:string="";
    email:string="";
    createPass:string="";

    newUser:User=new User();

    regsecess(){

      this.newUser.name=this.name;
      this.newUser.address=this.address;
      this.newUser.email=this.email;
      this.newUser.createPass=this.createPass;
       this.reguserdata.push(this.newUser);
       console.log(this.reguserdata);
       this.newUser=new User();
    }



}
<!-- <p>userregistraion works!</p> -->

Name:<br>
<input type="text" [(ngModel)]="name" placeholder="typeusername"/>
<br>
Address:<br>
<input type="text" [(ngModel)]="address" placeholder="take address"/>
<br>
Email:<br>
<input type="text" [(ngModel)]="email" placeholder="email"/>
<br>
CreatePass:<br>
<input type="text" [(ngModel)]="createPass" name="" placeholder="createYourPass"/>
<br>
<button (click)="regsecess()">Register</button>
