import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'registrationproject';

  name:string="this is my data";
  obj={
    fname:"vishal",
    lname:"deore"
   }
  whatToShow:number=0;
  display(num:number){
    this.whatToShow=num;
  }
  getDataFromChild(eventVal:string){
    console.log("app com : "+eventVal);
    
  }
  setData(valueFromHtml:string){

  }
}

<button (click)="display(1)">log in</button>
<button (click)="display(2)">reserpass</button>
<button (click)="display(3)">register</button>

<button (click)="display(4)">test-app</button>

<div *ngIf="whatToShow==1">
  <app-userlogin></app-userlogin>
</div>

<div *ngIf="whatToShow==2">
  <app-userpassrreset></app-userpassrreset>

</div>

<div *ngIf="whatToShow==3">
  <app-userregistraion></app-userregistraion>

</div>


<div>
  <app-test *ngIf="whatToShow==4" [mytest]="name" [obj1]="obj" (myEvent)="getDataFromChild($event)"></app-test>
  
</div>

<input type="text" #box/>
<button (click)="setData(box.value)">setDataInService</button>
